import React from 'react'
import { useState } from 'react';

export default function Inputs() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isValidEmail, setIsValidEmail] = useState(true);
  // const [inputBorderColor, setInputBorderColor] = useState('1px solid #ccc');
  
  const inputStyle = {
    borderColor: isValidEmail ? 'initial' : 'red',
  };

  const validateEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
    
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate the email address
    if (validateEmail(email)) {
      // Email is valid, you can perform further actions here
      setIsValidEmail(true);
      // Do something with the valid email, e.g., send it to a server
      console.log('Email is valid:', email);
    } else {
      // Email is invalid
      setIsValidEmail(false);
    }
  };
  return (
  <>
    <input 
      type='text'
      placeholder='First Name'
      value={firstName}
      onChange={(e) => setFirstName(e.target.value)}
    />
    <input 
      type='text'
      placeholder='Last Name'
      value={lastName}
      onChange={(e) => setLastName(e.target.value)}
    />
    <input 
      type='text'
      placeholder='Email'
      value={email}
      onChange={(e) => setEmail(e.target.value)}    
      style={inputStyles}
      />
    <input 
      type='password'
      placeholder='Password'
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    <form onSubmit={handleSubmit}>
      <button type='submit' className='claim-btn'>CLAIM YOUR FREE TRIAL</button>
    </form>
  </>
  )
}
