import './App.css';
import { useState } from 'react';
import error from './images/icon-error.svg';

function App() {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [isEmpty, setIsEmpty] = useState(false);

  const handleInputChange = (e) => {
    setFirstName(e.target.value);
    setIsEmpty(false); // Reset isEmpty when the input changes
  };
  const handleSubmit = (e) => {
    if (firstName.trim() === '') {
      setIsEmpty(true);
    }
  };
  const handleOtherButtonClick = () => {
    handleSubmit(); // Trigger form submission logic
  };

  const mediaStyles = {
    input: {
      borderColor: isEmpty ? 'red' : 'initial',
    },
    error: {
      position: 'absolute',
      right: '10px',
      top: '50%',
      transform: 'translateY(-50%)',
      width: '20px',
      height: '20px',
    },
    // Add @media queries directly to the style object
    '@media (max-width: 768px)': {
      input: {
        fontSize: '14px',
      },
    },
    '@media (min-width: 769px)': {
      input: {
        fontSize: '18px',
      },
    },
  };


  return (
    <>
      {/* Info box */}
      <header>
        <h1>Learn to code by watching others</h1>
        <h4>
          See how experienced developers solve 
          problems in real-time. Watching scripted 
          tutorials is great, but understanding 
          how developers think is invaluable. 
        </h4>
      </header>
      {/* Offer box */}
      <section>
        <div className='purple-div'>
          <p className='purple-div-p'>
            <b>Try it free 7 days </b>
            then $20/mo. thereafter
          </p>
        </div>
        {/* Input box */}
        <div className='white-box'>
          {/* First name */}
          <form onSubmit={handleSubmit}>
            <input 
              type='text'
              placeholder='firstname'
              value={firstName}
              onChange={handleInputChange}
              style={{ borderColor: isEmpty ? 'red' : 'initial' }}
            />
            {isEmpty && 
              <p style={
                { 
                  color: 'red', 
                  marginTop: '-12px',
                  fontSize: '11px',
                  fontStyle: 'italic',
                  color: '#FF7979',
                  textAlign: 'right',
                  marginBottom: '-15px'
                }}>
                First Name cannot be empty
              </p>
            } 
            {isEmpty && (
              <img
                src={error}
                alt="Warning"
                style={{
                    position: 'relative',
                    right: '-470px',
                    top: '-44px',
                    width: '20px',
                    height: '20px',
                    //Add @media queries within style
                    ...
                    {'@media (min-width: 1000px)': {
                      position: 'relative',
                      right: '-450px',
                      top: '-44px',
                      width: '20px',
                      height: '20px'
                    }}
                }}
              />
            )}
          </form>
















          {/* Last name */}
          <input 
            type='text'
            placeholder='lastname'
            value={lastName}
            onChange={()=> setLastName()}
          />
          {/* Email */}
          <input 
            type='text'
            placeholder='email'
            value={email}
            onChange={()=> setEmail()}
          />
          {/* Password */}
          <input 
            type='text'
            placeholder='password'
            value={password}
            onChange={()=> setPassword()}
          />

          {/* Claim button */}
          <button onClick={handleOtherButtonClick} className='claim-btn'>CLAIM YOUR FREE TRIAL</button>
          {/* Terms and Services */}
          <p className='terms-and-services'>
            By clicking the button, you are 
            agreeing to our 
              <b className='orange-bold-txt'> Terms and Services</b>
          </p>
        </div>
      </section>
    </>
  );
}

export default App;
