import './App.css';
import Inputs from './inputs';
import error from './images/icon-error.svg';

function App() {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate the email address
    if (validateEmail(email)) {
      // Email is valid, you can perform further actions here
      setIsValidEmail(true);
      // Do something with the valid email, e.g., send it to a server
      console.log('Email is valid:', email);
    } else {
      // Email is invalid
      setIsValidEmail(false);
    }
  };
  return (
    <>
      {/* Info box */}
      <header>
        <h1>Learn to code by watching others</h1>
        <h4>
          See how experienced developers solve 
          problems in real-time. Watching scripted 
          tutorials is great, but understanding 
          how developers think is invaluable. 
        </h4>
      </header>
      {/* Offer box */}
      <section>
        <div className='purple-div'>
          <p className='purple-div-p'>
            <b>Try it free 7 days </b>
            then $20/mo. thereafter
          </p>
        </div>
        {/* Input box */}
        <div className='white-box'>
          <Inputs/>
          <form onSubmit={handleSubmit}>
            <button type='submit' className='claim-btn'>CLAIM YOUR FREE TRIAL</button>
          </form>

          <p className='terms-and-services'>
            By clicking the button, you are 
            agreeing to our 
              <b className='orange-bold-txt'> Terms and Services</b>
          </p>
        </div>
      </section>
    </>
  );
};


export default App;
