import './App.css';
import { useState } from 'react';

function App() {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');

  return (
    <>
      {/* Info box */}
      <header>
        <h1>Learn to code by watching others</h1>
        <h4>
          See how experienced developers solve 
          problems in real-time. Watching scripted 
          tutorials is great, but understanding 
          how developers think is invaluable. 
        </h4>
      </header>
      {/* Offer box */}
      <section>
        <div className='purple-div'>
          <p className='purple-div-p'>
            <b>Try it free 7 days </b>
            then $20/mo. thereafter
          </p>
        </div>
        {/* Input box */}
        <div className='white-box'>
          {/* First name */}
          <input 
            type='text'
            placeholder='firstname'
            value={firstName}
            onChange={()=> setFirstName()}
          />
          {/* Last name */}
          <input 
            type='text'
            placeholder='lastname'
            value={lastName}
            onChange={()=> setLastName()}
          />
          {/* Email */}
          <input 
            type='text'
            placeholder='email'
            value={email}
            onChange={()=> setEmail()}
          />
          {/* Password */}
          <input 
            type='text'
            placeholder='password'
            value={password}
            onChange={()=> setPassword()}
          />

          {/* Claim button */}
          <button className='claim-btn'>CLAIM YOUR FREE TRIAL</button>
          {/* Terms and Services */}
          <p className='terms-and-services'>
            By clicking the button, you are 
            agreeing to our 
              <b className='orange'>Terms and Services</b>
          </p>
        </div>
      </section>
    </>
  );
}

export default App;
